// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

abstract contract ERC721 {
    function totalSupply() public virtual returns (uint256 total);
    function balanceOf(address _owner) public virtual returns (uint256 balance);
    function ownerOf(uint256 _tokenId) external virtual returns (address owner);
    function approve(address _to, uint256 _tokenId) external virtual;
    function transfer(address _to, uint256 _tokenId) external virtual;
    function transferFrom(address _from, address _to, uint256 _tokenId) external virtual;
    event Transfer(address from, address to, uint256 tokenId);
    event Approval(address owner, address approved, uint256 tokenId);
    function supportsInterface(bytes4 _interfaceID) external virtual returns (bool);
}




contract NFTPlatformAuction {
    struct Auction {
        address seller;
        uint256 minPrice;
        ERC721 NFTContract;
    }

    ERC721 public NFTContract;

    mapping(uint256 => Auction) public auctions;
}
